import React from 'react';
import { Outlet, useNavigate } from 'react-router-dom';
import {
  Box,
  Flex,
  Container,
  Stack,
  Button,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  useDisclosure,
  useColorModeValue,
  Text,
} from '@chakra-ui/react';
import { ChevronDownIcon } from '@chakra-ui/icons';

const MainLayout = () => {
  const navigate = useNavigate();
  const { isOpen, onToggle } = useDisclosure();
  
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login', { replace: true });
  };

  return (
    <Box minH="100vh" bg={useColorModeValue('gray.50', 'gray.900')}>
      {/* Navbar */}
      <Box bg={useColorModeValue('white', 'gray.800')} px={4} boxShadow="sm">
        <Flex h={16} alignItems="center" justifyContent="space-between">
          <Text fontSize="2xl" fontWeight="bold" color="blue.500">
            Sistema de Gestión
          </Text>

          <Flex alignItems="center">
            <Stack direction="row" spacing={7}>
              <Menu>
                <MenuButton
                  as={Button}
                  rightIcon={<ChevronDownIcon />}
                  variant="ghost"
                >
                  Menú
                </MenuButton>
                <MenuList>
                  <MenuItem onClick={() => navigate('/')}>Dashboard</MenuItem>
                  <MenuItem onClick={() => navigate('/punto-venta')}>Punto de Venta</MenuItem>
                  <MenuItem onClick={() => navigate('/clientes')}>Clientes</MenuItem>
                  <MenuItem onClick={() => navigate('/devoluciones')}>Devoluciones</MenuItem>
                  <MenuItem onClick={handleLogout} color="red.500">Cerrar Sesión</MenuItem>
                </MenuList>
              </Menu>
            </Stack>
          </Flex>
        </Flex>
      </Box>

      {/* Contenido principal */}
      <Container maxW="container.xl" py={8}>
        <Outlet />
      </Container>
    </Box>
  );
};

export default MainLayout;
